function liveFeed(t,e,p){$.get("https://public-api.wordpress.com/rest/v1/sites/"+t+"/posts?number="+e+"&tag="+p,function(t){var e=$("#content-template").html(),p=Handlebars.compile(e);console.log("Total Posts:"+t.posts.length),$.each(t.posts,function(t,e){if(20>t){var s={id:t,title:e.title,content:e.content.replace("https","http").replace("https","http").replace("https","http").replace("https","http").replace("https","http").split("?")[0]},o=p(s);$("#feed-content").append(o)}});for(var s=1;s<t.posts.length;s++);})}function AnimateObject(t){var e=$("#"+t);$("#shell").animate({scrollTop:e.offset().top},2e3).delay(1200)}